const MSAL_CLIENT_ID = "0652ef4a-cd47-4994-91ac-afbb86a8a2ba";
const MSAL_TENANT_ID = "ae0b754a-1500-4ce6-b39e-59d483b9ae9c";
const SHAREPOINT_SITE  = "https://stcanaveral.sharepoint.com";
const SP_SITE_RELATIVE = "/sites/root"; // ajustar si el sitio tiene un path distinto

let msalInstance = null;
function getMsal() {
  if (!msalInstance) {
    msalInstance = new msal.PublicClientApplication({
      auth: {
        clientId: MSAL_CLIENT_ID,
        authority: `https://login.microsoftonline.com/${MSAL_TENANT_ID}`,
        redirectUri: window.location.origin,
      },
      cache: { cacheLocation: "sessionStorage", storeAuthStateInCookie: false },
    });
  }
  return msalInstance;
}

async function getSpToken() {
  const app = getMsal();
  const accounts = app.getAllAccounts();
  if (!accounts.length) throw new Error("No hay sesión activa");
  const req = {
    scopes: [`${SHAREPOINT_SITE}/.default`],
    account: accounts[0],
  };
  try {
    const res = await app.acquireTokenSilent(req);
    return res.accessToken;
  } catch {
    const res = await app.acquireTokenPopup(req);
    return res.accessToken;
  }
}

