// ─── Login Screen (Microsoft 365 real) ────────────────────────────────────────
function LoginScreen({ onLogin, admins, viewers, sedes }) {
  const [loading, setLoading] = useState(false);
  const [error, setError]     = useState("");
  const [needSede, setNeedSede] = useState(null); // msUser pendiente de asignar sede
  const [sedeSelected, setSedeSelected] = useState("");

  const sedesActivas = (sedes || []).filter(s => s.activa);

  const resolveUser = (msUser, admins, viewers, sedeId) => {
    const email = msUser.username?.toLowerCase() || "";
    const nombre = msUser.name || email;
    if (admins.some(a => a.email?.toLowerCase() === email)) {
      return { id: email, nombre, email, rol: "admin", sedeId: null };
    }
    if (viewers.some(v => v.email?.toLowerCase() === email)) {
      return { id: email, nombre, email, rol: "viewer", sedeId: null };
    }
    // Seguridad — necesita sedeId
    return { id: email, nombre, email, rol: "security", sedeId: sedeId || null };
  };

  const handleMicrosoftLogin = async () => {
    setError("");
    setLoading(true);
    try {
      const app = getMsal();
      await app.initialize();
      const res = await app.loginPopup({
        scopes: ["User.Read", `${SHAREPOINT_SITE}/.default`],
        prompt: "select_account",
      });
      const msUser = res.account;
      const email  = msUser.username?.toLowerCase() || "";
      const isAdmin  = admins.some(a => a.email?.toLowerCase() === email);
      const isViewer = viewers.some(v => v.email?.toLowerCase() === email);
      if (isAdmin || isViewer) {
        onLogin(resolveUser(msUser, admins, viewers, null));
      } else {
        // Guardia de seguridad → elegir sede
        setNeedSede(msUser);
      }
    } catch (e) {
      if (e.errorCode !== "user_cancelled") {
        setError("Error al iniciar sesión: " + (e.message || e.errorCode || "intenta de nuevo"));
      }
    }
    setLoading(false);
  };

  const handleConfirmSede = () => {
    if (!needSede || !sedeSelected) return;
    onLogin(resolveUser(needSede, admins, viewers, sedeSelected));
  };

  return (
    <div style={{ minHeight: "100vh", background: "#05200b", display: "flex", flexDirection: "column", alignItems: "center", justifyContent: "center", padding: 24 }}>
      <div style={{ width: "100%", maxWidth: 420 }}>
        {/* Logo */}
        <div style={{ textAlign: "center", marginBottom: 36 }}>
          <img src={LOGO_SRC} alt="Supertiendas Cañaveral"
            style={{ width: 120, height: 120, borderRadius: "50%", objectFit: "cover", boxShadow: "0 8px 32px rgba(0,0,0,.4)", border: "3px solid rgba(255,255,255,.15)", marginBottom: 16 }} />
          <h1 style={{ color: "#fff", fontSize: 26, fontWeight: 900, letterSpacing: -0.5 }}>Control de Mercaderistas</h1>
          <p style={{ color: T.muted, fontSize: 13, marginTop: 6, fontStyle: "italic", lineHeight: 1.5 }}>
            "Alimentando familias y sirviendo con amor"
          </p>
        </div>

        <div style={{ background: "#0a2e10", borderRadius: 16, padding: 24, border: `1px solid rgba(255,255,255,.1)` }}>
          {!needSede ? (
            <>
              <p style={{ color: T.muted, fontSize: 12, fontWeight: 700, textTransform: "uppercase", letterSpacing: 1, marginBottom: 18, textAlign: "center" }}>
                Inicia sesión con tu cuenta corporativa
              </p>
              <button onClick={handleMicrosoftLogin} disabled={loading} style={{
                width: "100%", padding: "16px", borderRadius: 12, border: "none",
                background: loading ? "#334155" : T.amber,
                color: loading ? T.muted : T.navy,
                fontSize: 15, fontWeight: 800, cursor: loading ? "not-allowed" : "pointer",
                transition: "all .2s",
                display: "flex", alignItems: "center", justifyContent: "center", gap: 10
              }}>
                {loading ? <Spinner /> : <span style={{ fontSize: 20 }}>⊞</span>}
                {loading ? "Conectando..." : "Iniciar sesión con Microsoft"}
              </button>
              {error && (
                <div style={{ marginTop: 14, background: "rgba(239,68,68,.1)", border: "1px solid rgba(239,68,68,.3)", borderRadius: 8, padding: "10px 14px", color: "#fca5a5", fontSize: 13 }}>
                  {error}
                </div>
              )}
              <p style={{ textAlign: "center", color: T.muted, fontSize: 11, marginTop: 14 }}>
                Autenticación segura vía Azure AD · @stcanaveral.com
              </p>
            </>
          ) : (
            <>
              <div style={{ textAlign: "center", marginBottom: 18 }}>
                <div style={{ fontSize: 32, marginBottom: 8 }}>🛡️</div>
                <p style={{ color: "#fff", fontSize: 15, fontWeight: 700 }}>¡Hola, {needSede.name}!</p>
                <p style={{ color: T.muted, fontSize: 13, marginTop: 4 }}>Selecciona tu sede para continuar</p>
              </div>
              <select value={sedeSelected} onChange={e => setSedeSelected(e.target.value)} style={{
                width: "100%", padding: "12px 14px", borderRadius: 10, marginBottom: 16,
                border: `1.5px solid ${sedeSelected ? T.amber : "#334155"}`,
                background: "#0a2e10", color: sedeSelected ? "#fff" : T.muted,
                fontSize: 14, outline: "none"
              }}>
                <option value="">-- Selecciona tu sede --</option>
                {sedesActivas.map(s => (
                  <option key={s.id} value={s.id}>{s.nombre}</option>
                ))}
              </select>
              <button onClick={handleConfirmSede} disabled={!sedeSelected} style={{
                width: "100%", padding: "16px", borderRadius: 12, border: "none",
                background: sedeSelected ? T.amber : "#334155",
                color: sedeSelected ? T.navy : T.muted,
                fontSize: 15, fontWeight: 800, cursor: sedeSelected ? "pointer" : "not-allowed",
                transition: "all .2s"
              }}>
                Ingresar a {sedesActivas.find(s => s.id === sedeSelected)?.nombre || "mi sede"}
              </button>
              <button onClick={() => { setNeedSede(null); getMsal().logoutPopup(); }} style={{
                width: "100%", marginTop: 10, padding: "10px", borderRadius: 10,
                border: `1px solid #334155`, background: "transparent", color: T.muted,
                fontSize: 13, cursor: "pointer"
              }}>
                Cambiar cuenta
              </button>
            </>
          )}
        </div>
      </div>
    </div>
  );
}

